import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Modal from 'react-modal';
import { makeStyles } from '@material-ui/core/styles';
import Input from '@material-ui/core/Input';


const useStyles = makeStyles(theme => ({
    container: {
        display: 'flex',
        flexWrap: 'wrap',
    },
    input: {
        margin: theme.spacing(1),
    },
}));

class Register extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userdata: [],
            worktime: "早"
        }
    }
    componentDidMount() {
        //fetch
        //ユーザーデータ取得
        //fetch('/userdata')
        fetch('/testuserdata')
            .then(res => res.json())
            .then(data => this.setState({ userdata: data }))
    }
    render() {
        return (
            <div>
                <h1>新規登録</h1>
                <div className="profileholder">
                    <div className="profilesetting">
                        <div className="profilecard">
                            <img src={this.state.userdata.picture} id="profileimage"></img>
                            <p id="linename">{this.state.userdata.displayName}</p>
                        </div>
                        <div>
                            ユーザー名
                            <Input
                                id="username"
                                value={this.state.username}
                                onChange={this.onChangeusername}
                                className={useStyles.input}
                                inputProps={{
                                    'aria-label': 'username',
                                }}
                            />
                        </div>
                        <div className="worktime">
                            勤務区分:
                        <span>
                                <input type="radio" name="worktime" value="早" checked={this.state.worktime === '早'}
                                    onChange={() => this.setState({ worktime: '早' })} />早番
                            <input type="radio" name="worktime" value="遅" checked={this.state.worktime === '遅'}
                                    onChange={() => this.setState({ worktime: '遅' })} />遅番
                            <button className="bluebutton">更新</button>
                            </span>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Register;
