import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Calendar from 'react-calendar';
import Modal from 'react-modal';

const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)'
    }
};
Modal.setAppElement('#root')

class Homelocal extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userdata: [],
            date: new Date(),//today
            receptionDate: new Date(),//受付中
            //clickdate: "",//クリック
            stamp: "x",
            stamptime: "",
            calendarlist: [],
            shiftdatas: [],
            default_month_days: [],
            month_days: {
                20190814: { text: 'x' },
                20190820: { text: '17:00-23:00' }
            },
            shifttimes: ["09:00", "09:30", "10:00", "10:30", "11:00", "11:30", "12:00", "12:30",
                "13:00", "13:30", "14:00", "14:30", "15:00", "15:30",
                "16:00", "16:30", "17:00", "17:30", "18:00", "18:30",
                "19:00", "19:30", "20:00", "20:30", "21:00", "21:30",
                "22:00", "22:30", "23:00", "23:30", "23:45"],
            startTime: "09:00",
            endTime: "09:00",
            modalIsOpen: false
        }
        this.setdefaultshifts = this.setdefaultshifts.bind(this);
        this.getDateReception = this.getDateReception.bind(this);
        this.openModal = this.openModal.bind(this);
        this.afterOpenModal = this.afterOpenModal.bind(this);
        this.closeModal = this.closeModal.bind(this);
        this.setShiftTime = this.setShiftTime.bind(this);
        this.startTimeChange = this.startTimeChange.bind(this);
        this.endTimeChange = this.endTimeChange.bind(this);
    }
    componentDidMount() {
        //ユーザーデータ取得
        fetch('/testuserdata')
            .then(res => res.json())
            .then(data => this.setState({ userdata: data }))
        fetch('/testshiftdata')
            .then(res => res.json())
            .then(data => { this.setState({ shiftdatas: data }); this.setdefaultshifts(data) })
        this.getDate();
        this.getDateReception();

    }
    render() {
        return (
            <div>
                <h1>シフト希望</h1>
                ようこそ{this.state.userdata.username}さん
                <Link to="/Setting">設定</Link>

                <br></br>data確認------
                <div>{this.state.userdata.username}</div>
                <div>{this.state.userdata.userId}</div>
                <div>{this.state.userdata.displayName}</div>
                <div>{this.state.userdata.picture}</div>
                <div>{this.state.userdata.worktime}</div>
                <div>{this.state.userdata.administer}</div>
                ----------<br></br>
                <h2>{this.state.date.getFullYear()} {this.state.date.getMonth() + 1}月
                {this.state.receptionDate.getDate()}日～受付中</h2>

                <span id="dateText"></span>
                <button id="batu" onClick={this.onClickstamp.bind(this, "x")}>×</button>
                <button id="sankaku" onClick={this.onClickstamp.bind(this, "△")}>△</button>
                <button id="timeset" onClick={this.onClickstamp.bind(this, "time")}>Time</button>
                <div id="calendar"></div>
                <Calendar
                    locale="ja-JP"
                    calendarType="US"
                    value={this.state.date}
                    tileContent={this.getTileContent.bind(this)}
                    onChange={(value) => this.dayClick(value)}
                />

                <div>
                    補足希望
                    <form>
                        <span name="dataday" data-dataday="8/1"></span>
                        <input type="textarea" name="comment"></input>
                        <button>登録</button>
                    </form>
                </div>

                <Modal
                    isOpen={this.state.modalIsOpen}
                    onAfterOpen={this.afterOpenModal}
                    onRequestClose={this.closeModal}
                    style={customStyles}
                    contentLabel="Example Modal"
                >
                    <h2>出勤可能時間を選択</h2>
                    <div>
                        <select onChange={this.startTimeChange} value={this.state.startTime}>
                            {this.state.shifttimes.map(time =>
                                <option key={time}>{time}</option>
                            )}
                        </select>～
                    <select onChange={this.endTimeChange} value={this.state.endTime}>
                            {this.state.shifttimes.map(time =>
                                <option key={time}>{time}</option>
                            )}
                        </select>
                    </div>
                    <button onClick={this.closeModal}>閉じる</button>
                    <button onClick={this.setShiftTime}>決定</button>
                </Modal>
            </div>
        );
    }

    getDate() {
        const weeks = ['日', '月', '火', '水', '木', '金', '土']
        const date = new Date()
        let year = date.getFullYear()
        let month = date.getMonth() + 1
        console.log(year + "/" + month + "/" + date.getDate() + " " + weeks[date.getDay()]);
    }
    setdefaultshifts(data) {
        for (var shift of data) {
            console.log(shift)
        }
    }
    getFormatDate(date) {
        return `${date.getFullYear()}${('0' + (date.getMonth() + 1)).slice(-2)}${('0' + date.getDate()).slice(-2)}`;
    }

    //受付中日にち
    getDateReception() {
        let day = this.state.date.getDate();
        var setday = this.state.date.getFullYear() + "/" + (this.state.date.getMonth() + 1);
        if (day > 10 && day < 24) {
            setday += "/16";
            this.setState({
                receptionDate: new Date(setday)
            })
        } else {
            setday += "/1";
            this.setState({
                receptionDate: new Date(setday)
            })
        }
    }
    getTileContent({ date, view }) {
        // 月表示のときのみ
        if (view !== 'month') {
            return null;
        }
        const day = this.getFormatDate(date);
        return (
            <p>

                {(this.state.month_days[day] && this.state.month_days[day].text) ?
                    this.state.month_days[day].text : '  '
                }

            </p>
        );
    }

    onClickstamp(value) {
        this.setState({
            stamp: value
        })
    }
    //日付クリック
    dayClick(value) {
        //this.setState({ clickdate: value })
        //受付中か
        if (this.state.receptionDate <= value) {
            var dayslist = this.state.month_days;
            var clickday = value.getFullYear() + ("0" + (value.getMonth() + 1)).slice(-2) + ("0" + value.getDate()).slice(-2);
            this.setState({ clickdate: clickday })
            //空or重複判定
            if (null == dayslist[clickday] || "" == dayslist[clickday].text) {
                //stampが記号か日付で分岐
                if (this.state.stamp == "time") {
                    this.openModal();

                } else {
                    dayslist[clickday] = { text: this.state.stamp }
                    this.setState({ month_days: dayslist })
                }
                //stamp一致クリック
            } else if (this.state.stamp == dayslist[clickday].text) {
                dayslist[clickday] = { text: "" }
                this.setState({ month_days: dayslist })
            } else if (this.state.stamp != dayslist[clickday].text) {
                //stampが記号か日付で分岐
                if (this.state.stamp == "time") {
                    //this.openModal();
                    dayslist[clickday] = { text: "" }
                } else {
                    dayslist[clickday] = { text: this.state.stamp }
                    this.setState({ month_days: dayslist })
                }
            }

        }
    }
    openModal() {
        this.setState({ modalIsOpen: true });
    }
    afterOpenModal() {
        // references are now sync'd and can be accessed.
        //this.subtitle.style.color = '#f00';
    }
    closeModal() {
        this.setState({ modalIsOpen: false });
    }
    setShiftTime() {
        if (this.state.startTime >= this.state.endTime) {
            alert("不可能な時間です")
        } else {
            this.closeModal();
            var dayslist = this.state.month_days;
            dayslist[this.state.clickdate] = { text: this.state.startTime + "-" + this.state.endTime }
            this.setState({ month_days: dayslist });
        }
    }
    startTimeChange(e) {
        this.setState({
            startTime: e.target.value
        })
    }
    endTimeChange(e) {
        this.setState({
            endTime: e.target.value
        })
    }

}

export default Homelocal;
