import React, { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      dbcheck: []
    }
  }
  render() {
    return (
      <div className="App">
        <h1>ログイン</h1>
        <a href="/auth">
          LINE ログインテスト2
    </a><br></br>
        <button onClick={this.lineAuth}>ログイン</button>

        <p>
          <h2>tester</h2>
          ------------------<br></br>
          <a href="/callback">
            callback
        </a>
          <br></br>
          <a href="" onClick={this.getDBChecker()}>
            DBChecker
        </a>
          {this.state.dbcheck.map(data =>
            <div>
              {data}
            </div>
          )}

          <br></br>
          <a href="/dblocal">
            DBChecker in local
        </a>
          <br></br>
          <a href="/Homelocal">
            HomeChecker in local
        </a>
          <form method="post" action="/Home">
            formtester<br></br>
          </form>
          -------------------------
    </p>
      </div>
    );
  }

  getDBChecker() {
    fetch('/dblocal')
      .then(res => res.json())
      .then(data => { this.setState({ dbcheck: data }); console.log(data) })
  }
  lineAuth() {
    fetch('/auth')
      .then(res => res.json())
  }
}

export default App;
