import React, { Component } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles(theme => ({
    root: {
        width: '100%',
        marginTop: theme.spacing(3),
        overflowX: 'auto',
    },
    table: {
        minWidth: 650,
    },
}));

class Wishlist extends Component {
    constructor(props) {
        super(props);
        this.state = {
            query: this.props.location.query,
            accessable: false,
            memberlist: [],
            allshiftdata: []
        }

    }
    componentDidMount() {
        //アクセス判定
        if (this.props.location.query !== undefined) {
            if (this.props.location.query.pass === "administer") {
                this.setState({
                    accessable: true
                })
                //全ユーザー取得
                fetch('/testmemberlist')
                    //fetch('/memberlist')
                    .then(res => res.json())
                    .then(list => {
                        this.setState({ memberlist: list })
                        //全シフト情報取得
                        fetch('/testallshiftdata')
                            //fetch('/allshiftdata')
                            .then(res => res.json())
                            .then(data => this.setState({ allshiftdata: data }))
                    })

            }
        }
    }
    render() {
        const classes = useStyles;
        return (
            <div>
                {this.state.accessable ? (
                    <div>
                        <h1>Wishlist</h1>
                        アクセス可能
                        <Paper className={classes.root}>
                            <Table className={classes.table}>
                                <TableHead>
                                    <TableRow>
                                        <TableCell>name</TableCell>
                                        <TableCell align="right">userid</TableCell>
                                        <TableCell align="right">worktime</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {this.state.memberlist.map(row => (
                                        <TableRow key={row.name}>
                                            <TableCell component="th" scope="row">
                                                {row.name}
                                            </TableCell>
                                            <TableCell align="right">{row.userid}</TableCell>
                                            <TableCell align="right">{row.worktime}</TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </Paper>
                    </div>
                ) : (
                        <div>アクセスできません</div>
                    )}
            </div>

        );
    }
}

export default Wishlist;
