import React, { Component } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles(theme => ({
    root: {
        width: '100%',
        marginTop: theme.spacing(3),
        overflowX: 'auto',
    },
    table: {
        minWidth: 650,
    },
}));

class Wishlist extends Component {
    constructor(props) {
        super(props);
        this.state = {
            query: this.props.location.query,
            accessable: false,//アクセス判定
            memberlist: [],//登録メンバー
            allshiftdata: [],//全シフト情報
            deadlineearly: 10,
            deallinelate: 24,
            printdays: []//表示する期間
        }

        this.setprintDays = this.setprintDays.bind(this);
    }
    componentDidMount() {
        //アクセス判定
        if (this.props.location.query !== undefined) {
            if (this.props.location.query.pass === "administer") {
                this.setState({
                    accessable: true
                })
                this.setprintDays();
                //全ユーザー取得
                fetch('/testmemberlist')
                    //fetch('/memberlist')
                    .then(res => res.json())
                    .then(list => {
                        this.setState({ memberlist: list })
                        //全シフト情報取得
                        fetch('/testallshiftdata')
                            //fetch('/allshiftdata')
                            .then(res => res.json())
                            .then(data => this.setState({ allshiftdata: data }))
                    })

            }
        }
    }
    render() {
        const classes = useStyles;
        return (
            <div>
                {this.state.accessable ? (
                    <div>
                        <h1>Wishlist</h1>
                        アクセス可能
                        <Paper className={classes.root}>
                            <Table className={classes.table}>
                                <TableHead>
                                    <TableRow>
                                        <TableCell>name</TableCell>
                                        <TableCell align="right">userid</TableCell>
                                        <TableCell align="right">worktime</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {this.state.memberlist.map(row => (
                                        <TableRow key={row.name}>
                                            <TableCell component="th" scope="row">
                                                {row.name}
                                            </TableCell>
                                            <TableCell align="right">{row.userid}</TableCell>
                                            <TableCell align="right">{row.worktime}</TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </Paper>
                    </div>
                ) : (
                        <div>アクセスできません</div>
                    )}
            </div>

        );
    }
    setprintDays() {
        //表示する期間開始日を計算
        const date = new Date();
        const year = date.getFullYear();
        const month = date.getMonth();
        const day = 11//date.getDate();
        let startdate = "";
        if (day < this.state.deadlineearly) {
            startdate = new Date(year, month, 1);
        } else if (day < this.state.deallinelate) {
            startdate = new Date(year, month, 15);
        } else if (day >= this.state.deallinelate) {
            if (month === 11) {
                startdate = new Date(year + 1, month + 1, 1);
            } else {
                startdate = new Date(year, month + 1, 1);
            }
        }
        console.log(startdate);
        const days = [];
        if (startdate.getDate() === 1) {
            for (let i = 0; i < 15; i++) {
                days.push((startdate.getMonth() + 1) + "/" + (startdate.getDate() + i));
            }
        } else if (startdate.getDate() === 15) {
            const findate = new Date(startdate.getFullYear(), startdate.getMonth(), 0);
            for (let i = 0; i < findate.getDate() - startdate.getDate() + 1; i++) {
                days.push((startdate.getMonth() + 1) + "/" + (startdate.getDate() + i));
            }
        }
        console.log(days);
    }
}

export default Wishlist;
