import React, { Component } from 'react';
import { Link } from 'react-router-dom';

class Setting extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userdata: [],//ユーザー情報
            test: ""
        }
    }
    componentDidMount() {
        //ユーザーデータ取得
        fetch('/testuserdata')
            .then(res => res.json())
            .then(data => this.setState({ userdata: data }))
    }
    render() {
        return (
            <div>
                <h1>設定</h1>
                <img src={this.state.userdata.picture}></img>
                <p>{this.state.userdata.displayName}</p>
                <div>
                    ユーザー名
                <input value={this.state.userdata.username}></input>
                    <button>更新</button>
                </div>
                <div>
                    勤務区分:
                {this.state.userdata.worktime}番
                <button>変更</button>
                </div>
                <div style={{ display: this.state.userdata.administer ? '' : 'none' }}>
                    you are administer!
                </div>

            </div>
        );
    }
}

export default Setting;
