import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Modal from 'react-modal';

const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)'
    }
};
Modal.setAppElement('#root')

class Setting extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userdata: [],//ユーザー情報
            memberlist: [],
            test: "",
            memberModalopen: false
        }

        this.openmemberModal = this.openmemberModal.bind(this);
        this.closememberModal = this.closememberModal.bind(this);
    }
    componentDidMount() {
        //ユーザーデータ取得
        fetch('/testuserdata')
            .then(res => res.json())
            .then(data => {
                this.setState({ userdata: data });
                if (data.administer) {
                    //menbarlist
                    fetch('/testmemberlist')
                        .then(res => res.json())
                        .then(list => console.log(list)//this.setState({ memberlist: list })
                        );
                }
            })
    }
    render() {
        return (
            <div>
                <h1>設定</h1>
                <img src={this.state.userdata.picture}></img>
                <p>{this.state.userdata.displayName}</p>
                <div>
                    ユーザー名
                <input type="textarea" name="name" value={this.state.userdata.username}></input>
                    <button>更新</button>
                </div>
                <div>
                    勤務区分:
                {this.state.userdata.worktime}番
                <button>変更</button>
                </div>
                <div style={{ display: this.state.userdata.administer ? '' : 'none' }}>
                    管理者権限<br></br>
                    <button>希望一覧</button>
                    <button onClick={this.openmemberModal}>登録者一覧</button>
                    <button>管理者一覧</button>
                </div>
                <Modal
                    isOpen={this.state.memberModalopen}
                    onRequestClose={this.closememberModal}
                    style={customStyles}
                    contentLabel="Example Modal"
                >
                    <h2>登録者一覧</h2>
                    {this.state.memberlist.map((member) =>
                        <div>
                            {member.username}
                        </div>)}
                    <button onClick={this.closememberModal}>閉じる</button>
                </Modal>

            </div>
        );
    }


    openmemberModal() {
        this.setState({ memberModalopen: true });
    }
    closememberModal() {
        this.setState({ memberModalopen: false });
    }
}

export default Setting;
