import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Modal from 'react-modal';

const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)'
    }
};
Modal.setAppElement('#root')

class Setting extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userdata: [],//ユーザー情報
            username: "",
            memberlist: [],
            ischangeworktime: false,
            test: "",
            memberModalopen: false
        }

        this.onChangeusername = this.onChangeusername.bind(this);
        this.updateUsername = this.updateUsername.bind(this);
        this.onChangeworktime = this.onChangeworktime.bind(this);

        this.openmemberModal = this.openmemberModal.bind(this);
        this.closememberModal = this.closememberModal.bind(this);
    }
    componentDidMount() {
        //ユーザーデータ取得
        fetch('/testuserdata')
            .then(res => res.json())
            .then(data => {
                this.setState({
                    userdata: data,
                    username: data.username
                });
                if (data.administer) {
                    //menbarlist
                    fetch('/testmemberlist')
                        .then(res => res.json())
                        .then(list => this.setState({ memberlist: list })
                        );
                }
            })
    }
    render() {
        return (
            <div>
                <h1>設定</h1>
                <img src={this.state.userdata.picture}></img>
                <p>{this.state.userdata.displayName}</p>
                <div>
                    ユーザー名
                <input type="textarea" name="name" value={this.state.username} onChange={this.onChangeusername}></input>
                    <button onClick={this.updateUsername}>更新</button>
                </div>
                <div>
                    勤務区分:
                {this.state.ischangeworktime ? (
                        <span>
                            <button>更新</button>
                        </span>
                    ) : (
                            <span>
                                {this.state.userdata.worktime}番
                                <button onClick={this.onChangeworktime}>変更</button>
                            </span>
                        )}

                </div>
                <div style={{ display: this.state.userdata.administer ? '' : 'none' }}>
                    管理者権限<br></br>
                    <button>希望一覧</button>
                    <button onClick={this.openmemberModal}>登録者一覧</button>
                    <button>管理者一覧</button>
                </div>
                <Modal
                    isOpen={this.state.memberModalopen}
                    onRequestClose={this.closememberModal}
                    style={customStyles}
                    contentLabel="Example Modal"
                >
                    <h2>登録者一覧</h2>
                    {this.state.memberlist.map((member) =>
                        <div>
                            {member.name}<button>del</button>
                        </div>)}
                    <button onClick={this.closememberModal}>閉じる</button>
                </Modal>

            </div>
        );
    }

    //ユーザー名変更
    onChangeusername(e) {
        this.setState({
            username: e.target.value
        })
    }
    //ユーザー名変更処理
    updateUsername() {
        if (this.state.username != this.state.userdata.username) {
            //console.log("update username!");
            //更新fetch
            fetch('/testupdateusername', {
                method: 'POST',
                body: JSON.stringify([this.state.username]),
                headers: {
                    'Content-Type': 'application/json'
                },
                mode: 'cors'
            }).then(res => res.json())
                .then(str => {
                    alert("登録しました");
                    this.state.userdata.username = this.state.username;
                })
        }
    }
    //勤務区分変更判定
    onChangeworktime() {
        this.setState({
            ischangeworktime: true
        })
    }

    openmemberModal() {
        this.setState({ memberModalopen: true });
    }
    closememberModal() {
        this.setState({ memberModalopen: false });
    }
}

export default Setting;
